import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; 
import '../components/Login.css'; 

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        // Enviar los datos al backend para la autenticación
        try {
            const response = await fetch('http://localhost:3001/api/usuarios/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ email, password })
            });

            if (response.ok) {
                const data = await response.json();
                // Aquí puedes almacenar el token o datos de usuario en localStorage, etc.
                localStorage.setItem('token', data.token);
                navigate('/dashboard'); // Redirigir a un dashboard o página de inicio
            } else {
                setErrorMessage('Credenciales incorrectas');
            }
        } catch (error) {
            console.error('Error:', error);
            setErrorMessage('Error al conectar con el servidor');
        }
    };

    return (
        <div className="login-background">
            <div className="login-container">
                <form className="login-form" onSubmit={handleLogin}>
                    <h2>Inicio de Sesión</h2>
                    <p>
                        Si ya tienes una cuenta inicia sesión, sino{' '}
                        <Link to="/register">crea tu cuenta aquí</Link>.
                    </p>
                    <div className="input-container">
                        <input
                            type="email"
                            placeholder="Correo electrónico"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="input-container">
                        <input
                            type="password"
                            placeholder="Contraseña"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="login-button">Ingresar</button>
                    {errorMessage && <p>{errorMessage}</p>}
                </form>
            </div>
        </div>
    );
}

export default Login;
