import React, { useState } from 'react';
import '../components/RegisterEvent.css';


const RegisterEvent = () => {
    const [formData, setFormData] = useState({
        nombreEvento: '',
        urlEvento: '',
        descripcionEvento: '',
        tipoEvento: '',
        organizadores: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Datos del evento:', formData);
        // Aquí puedes agregar la lógica para enviar los datos al backend
    };

    return (
        <form className="register-event-form" onSubmit={handleSubmit}>
            <h2>Registra tu evento</h2>
            <p>Ingresa la informacion acerca del evento.</p>

            <label>Nombre del evento</label>
            <input
                type="text"
                name="nombreEvento"
                placeholder="Nombre del evento"
                value={formData.nombreEvento}
                onChange={handleChange}
                required
            />

            <label>URL del evento</label>
            <input
                type="text"
                name="urlEvento"
                placeholder="URL del evento"
                value={formData.urlEvento}
                onChange={handleChange}
                required
            />

            <label>Descripción del evento</label>
            <input
                type="text"
                name="descripcionEvento"
                placeholder="Descripcion del evento"
                value={formData.descripcionEvento}
                onChange={handleChange}
                required
            />

            <label>Tipo de evento</label>
            <input
                type="text"
                name="tipoEvento"
                placeholder="Tipo del evento"
                value={formData.tipoEvento}
                onChange={handleChange}
                required
            />

            <label>Organizadores</label>
            <div className="organizers-input">
                <input
                    type="text"
                    name="organizadores"
                    placeholder="Organizadores del evento"
                    value={formData.organizadores}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Agregar</button>
            </div>
        </form>
    );
};

export default RegisterEvent;
