import React, { useState } from 'react';
import './Register.css';
import logo from '../img/LogoUticket.png';
import { Link, useNavigate } from 'react-router-dom'; // Usamos useNavigate para redirigir
import axios from 'axios'; // Asegúrate de instalar axios

const Register = () => {
  const [formData, setFormData] = useState({
    nombre: '',
    apellido: '',
    celular: '',
    cedula: '',
    email: '',
    password: '',
    repeatPassword: ''
  });

  const [passwordMatchError, setPasswordMatchError] = useState('');
  const [passwordRequirementsError, setPasswordRequirementsError] = useState('');
  const [loading, setLoading] = useState(false); // Para mostrar un estado de carga
  const [error, setError] = useState(''); // Para manejar errores del servidor

  const navigate = useNavigate(); // Redirigir después de un registro exitoso

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validación de la contraseña
  const validatePassword = () => {
    const { password, repeatPassword } = formData;

    if (password !== repeatPassword) {
      setPasswordMatchError('Las contraseñas no coinciden');
      return false;
    } else {
      setPasswordMatchError('');
    }

    const passwordRegex = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{2,4}$/;
    if (!passwordRegex.test(password)) {
      setPasswordRequirementsError('La contraseña debe tener entre 2 y 4 caracteres, una letra mayúscula, un número y un carácter especial.');
      return false;
    } else {
      setPasswordRequirementsError('');
    }

    return true;
  };

  // Validación general
  const validateForm = () => {
    const { nombre, apellido, celular, cedula, email, password, repeatPassword } = formData;

    // Validar campos vacíos
    if (!nombre || !apellido || !celular || !cedula || !email || !password || !repeatPassword) {
      setError('Por favor, completa todos los campos');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validar la contraseña y otros campos
    if (validateForm() && validatePassword()) {
      setLoading(true); // Activar el estado de carga
      try {
        // Enviar la solicitud al backend para crear el nuevo usuario
        const response = await axios.post('http://localhost:3001/api/usuarios', formData); // Ajusta la URL según tu backend

        // Si el registro es exitoso, navega a la página de login o dashboard
        if (response.status === 201) {
          navigate('/login'); // Redirige al login después del registro exitoso
        }
      } catch (error) {
        if (error.response) {
          // Si el servidor responde con un error
          setError(`Error: ${error.response.data.message || 'Hubo un error al registrar la cuenta. Por favor, intenta nuevamente.'}`);
        } else if (error.request) {
          // Si la solicitud fue realizada pero no se obtuvo respuesta
          setError('No se recibió respuesta del servidor.');
        } else {
          // Otros errores
          setError(`Error: ${error.message}`);
        }
        setLoading(false);
      }
      
    }
  };

  return (
    <div className="register-background">
      <div className="register-container">
        <div className="form-section">
          <h2>Crear cuenta</h2>
          <p>
            Si eres nuevo crea tu cuenta, sino{' '}
            <Link to="/login">inicia sesión aquí</Link>.
          </p>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="nombre"
              placeholder="Ingresa tu nombre"
              value={formData.nombre}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="apellido"
              placeholder="Ingresa tus apellidos"
              value={formData.apellido}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="celular"
              placeholder="Ej: 7777777"
              value={formData.celular}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="cedula"
              placeholder="Ej: 1234567"
              value={formData.cedula}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Ingresa tu correo electrónico"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Contraseña"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="repeatPassword"
              placeholder="Repite tu contraseña"
              value={formData.repeatPassword}
              onChange={handleChange}
              required
            />

            {/* Mensajes de error */}
            {passwordMatchError && <p className="error">{passwordMatchError}</p>}
            {passwordRequirementsError && <p className="error">{passwordRequirementsError}</p>}
            {error && <p className="error">{error}</p>} {/* Error del servidor */}

            <button type="submit" disabled={loading}>
              {loading ? 'Registrando...' : 'Crear cuenta'}
            </button>
          </form>
        </div>
        <div className="logo-section">
          <img src={logo} alt="Logo Utickets" />
        </div>
      </div>
    </div>
  );
};

export default Register;
