import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../components/RegisterEvent.css';

const RegisterEvent = () => {
    const [formData, setFormData] = useState({
        nombreEvento: '',
        descripcionEvento: '',
        tipoEvento: '',
        categoriaEvento: '', // Agregar este campo
        aforo: '',
        sectores: {
            vip: false,
            general: false,
            preferencia: false,
        },
        costos: {
            costoVip: '',
            costoGeneral: '',
            costoPreferencia: ''
        }
    });

    const [categorias, setCategorias] = useState([]);

    const navigate = useNavigate();

    // Obtener categorías desde el backend
    useEffect(() => {
        fetch('http://localhost:3000/api/categorias')  // Cambia la URL si es necesario
            .then((response) => response.json())
            .then((data) => setCategorias(data))
            .catch((error) => console.error('Error al obtener categorías:', error));
    }, []);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (type === 'checkbox') {
            setFormData({
                ...formData,
                sectores: {
                    ...formData.sectores,
                    [name]: checked
                }
            });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleCostoChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            costos: { ...formData.costos, [name]: value }
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Datos del evento:', formData);
        navigate('/registereventdetails');
    };

    return (
        <form className="register-event-form" onSubmit={handleSubmit}>
            <h2>Registro de evento</h2>
            <p>Información Básica del evento, detalles que hacen únicos cada evento.</p>

            {/* Otros campos */}
            
            <label>Categoría del evento</label>
            <select
                name="categoriaEvento"
                value={formData.categoriaEvento}
                onChange={handleChange}
                required
            >
                <option value="">Seleccione una categoría</option>
                {categorias.map((categoria) => (
                    <option key={categoria.id} value={categoria.id}>
                        {categoria.nombre} {/* Asegúrate de que el nombre sea el correcto */}
                    </option>
                ))}
            </select>

            {/* Resto del formulario */}
            <button type="submit">Siguiente</button>
        </form>
    );
};

export default RegisterEvent;
