import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../components/RegisterEvent.css';

const RegisterEvent = ({ setEventData }) => {
    const [formData, setFormData] = useState({
        nombreEvento: '',
        descripcionEvento: '',
        tipoEvento: '',
        aforo: '',
        categoriaEventoId: '', // Para la categoría seleccionada
        sectores: {
            vip: false,
            general: false,
            preferencia: false,
        },
        costos: {
            costoVip: '',
            costoGeneral: '',
            costoPreferencia: ''
        }
    });

    const [categorias, setCategorias] = useState([]); // Nuevo estado para las categorías
    const navigate = useNavigate();

    // Llamada a la API para obtener las categorías
    useEffect(() => {
        const fetchCategorias = async () => {
            try {
                const response = await fetch('http://localhost:3001/api/categorias'); // Ajusta la URL según tu API
                const data = await response.json();
                setCategorias(data); // Guardar las categorías en el estado
            } catch (error) {
                console.error('Error al cargar las categorías:', error);
            }
        };

        fetchCategorias();
    }, []); // Este efecto solo se ejecuta cuando el componente se monta

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (type === "checkbox") {
            setFormData({
                ...formData,
                sectores: {
                    ...formData.sectores,
                    [name]: checked
                }
            });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleCostoChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            costos: { ...formData.costos, [name]: value }
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Enviar los datos al backend
        const evento = {
            categoria_evento_id: formData.categoriaEventoId,
            titulo: formData.nombreEvento,
            descripcion: formData.descripcionEvento,
            organizadores: formData.tipoEvento,
            ubicacion: formData.aforo,
            fecha_hora: new Date().toISOString(), // Ajusta según el formato de fecha
            precio_base: formData.costos.costoGeneral,
            cupo_disponible: formData.aforo,
            es_evento_virtual: false, // Ajustar según el tipo de evento
            url_transmision: '', // Si es virtual
            plataforma_virtual: '' // Si es virtual
        };

        try {
            const response = await fetch('http://localhost:3001/api/eventos', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(evento),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Evento creado con éxito:', data);
                navigate('/registereventdetails');
            } else {
                console.error('Error al crear el evento');
            }
        } catch (error) {
            console.error('Error al enviar los datos del evento:', error);
        }
    };

    return (
        <form className="register-event-form" onSubmit={handleSubmit}>
            <h2>Registro de evento</h2>
            <label>Nombre del evento</label>
            <input
                type="text"
                name="nombreEvento"
                placeholder="Nombre del evento"
                value={formData.nombreEvento}
                onChange={handleChange}
                required
            />

            <label>Descripción del evento</label>
            <input
                type="text"
                name="descripcionEvento"
                placeholder="Descripción del evento"
                value={formData.descripcionEvento}
                onChange={handleChange}
                required
            />

            <label>Tipo de evento</label>
            <input
                type="text"
                name="tipoEvento"
                placeholder="Tipo de evento"
                value={formData.tipoEvento}
                onChange={handleChange}
                required
            />

            <label>Aforo</label>
            <input
                type="number"
                name="aforo"
                placeholder="Cantidad"
                value={formData.aforo}
                onChange={handleChange}
                required
            />

            {/* Aquí agregas el combo de categoría de evento, cargado desde la API */}
            <label>Categoria de Evento</label>
            <select
                name="categoriaEventoId"
                value={formData.categoriaEventoId}
                onChange={handleChange}
                required
            >
                <option value="">Seleccione una categoría</option>
                {categorias.map((categoria) => (
                    <option key={categoria.id} value={categoria.id}>
                        {categoria.nombre}
                    </option>
                ))}
            </select>

            {/* Sectores y costos */}
            <div className="sectores">
                <div>
                    <input
                        type="checkbox"
                        name="vip"
                        checked={formData.sectores.vip}
                        onChange={handleChange}
                    />
                    <label>VIP</label>
                    {formData.sectores.vip && (
                        <input
                            type="number"
                            name="costoVip"
                            placeholder="Costo VIP"
                            value={formData.costos.costoVip}
                            onChange={handleCostoChange}
                        />
                    )}
                </div>
                <div>
                    <input
                        type="checkbox"
                        name="general"
                        checked={formData.sectores.general}
                        onChange={handleChange}
                    />
                    <label>General</label>
                    {formData.sectores.general && (
                        <input
                            type="number"
                            name="costoGeneral"
                            placeholder="Costo General"
                            value={formData.costos.costoGeneral}
                            onChange={handleCostoChange}
                        />
                    )}
                </div>
                <div>
                    <input
                        type="checkbox"
                        name="preferencia"
                        checked={formData.sectores.preferencia}
                        onChange={handleChange}
                    />
                    <label>Preferencia</label>
                    {formData.sectores.preferencia && (
                        <input
                            type="number"
                            name="costoPreferencia"
                            placeholder="Costo Preferencia"
                            value={formData.costos.costoPreferencia}
                            onChange={handleCostoChange}
                        />
                    )}
                </div>
            </div>

            <button type="submit">Siguiente</button>
        </form>
    );
};

export default RegisterEvent;
