import React, { useState, useEffect } from 'react';

const RegisterEvent = ({ setEventData }) => {
  const [formData, setFormData] = useState({
    nombreEvento: '',
    descripcionEvento: '',
    tipoEvento: '',
    aforo: '',
    categoriaEventoId: '',
    sectores: {
      vip: false,
      general: false,
      preferencia: false,
    },
    costos: {
      costoVip: '',
      costoGeneral: '',
      costoPreferencia: ''
    }
  });

  const [categorias, setCategorias] = useState([]);

  // Cargar las categorías del evento desde el backend
  useEffect(() => {
    const fetchCategorias = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/categorias');
        const data = await response.json();
        setCategorias(data);
      } catch (error) {
        console.error('Error al cargar las categorías:', error);
      }
    };

    fetchCategorias();
  }, []);

  // Función para manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setFormData({
        ...formData,
        sectores: {
          ...formData.sectores,
          [name]: checked
        }
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  // Enviar los datos al componente padre
  const handleSubmit = (e) => {
    e.preventDefault();
    setEventData(formData); // Pasa los datos al componente padre
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Registro de evento</h2>
      
      {/* Nombre del evento */}
      <label>Nombre del evento</label>
      <input
        type="text"
        name="nombreEvento"
        placeholder="Nombre del evento"
        value={formData.nombreEvento}
        onChange={handleChange}
        required
      />

      {/* Descripción del evento */}
      <label>Descripción del evento</label>
      <input
        type="text"
        name="descripcionEvento"
        placeholder="Descripción del evento"
        value={formData.descripcionEvento}
        onChange={handleChange}
        required
      />

      {/* Categoría del evento */}
      <label>Categoria de Evento</label>
      <select
        name="categoriaEventoId"
        value={formData.categoriaEventoId}
        onChange={handleChange}
        required
      >
        <option value="">Seleccione una categoría</option>
        {categorias.map((categoria) => (
          <option key={categoria.id} value={categoria.id}>
            {categoria.nombre}
          </option>
        ))}
      </select>

      {/* Botón para ir a la siguiente vista */}
      <button type="submit">Siguiente</button>
    </form>
  );
};

export default RegisterEvent;
