import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import logo from '../img/LogoUticket.png'; 
import '../pages/Login.css'; 

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const history = useHistory();  // Usar history para redirigir al usuario después del login

    const handleLogin = async (e) => {
        e.preventDefault();

        // Limpiar el mensaje de error antes de intentar el login
        setErrorMessage('');

        // Enviar los datos al backend para la autenticación
        const loginData = { correo_electronico: email, contrasena: password };

        try {
            const response = await fetch('http://localhost:3001/login', {  // Cambia la URL si es diferente
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(loginData),
            });

            const data = await response.json();

            if (response.ok) {
                // Guardar el token JWT en el almacenamiento local
                localStorage.setItem('token', data.token);

                // Redirigir a la página de inicio o al dashboard
                history.push('/home');  // Cambia '/home' por la ruta que desees
            } else {
                setErrorMessage(data.error || 'Error al iniciar sesión');
            }
        } catch (error) {
            console.error('Error al iniciar sesión:', error);
            setErrorMessage('Error al conectar con el servidor');
        }
    };

    return (
        <div className="login-background">
            <div className="login-container">
                <div className="logo-section">
                    <img src={logo} alt="Logo Utickets" />
                </div>

                <div className="form-section">
                    <h2>Bienvenido a <span className="highlight">UTICKET</span></h2>
                    
                    <button className="social-login google">Login with Google</button>
                    <button className="social-login facebook">Login with Facebook</button>

                    <form onSubmit={handleLogin}>
                        <div className="input-container">
                            <input
                                type="email"
                                placeholder="Ingresa tu correo electronico"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                            <input
                                type="password"
                                placeholder="Ingresa tu contraseña"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>

                        <div className="form-options">
                            <div className="remember-me">
                                <input type="checkbox" id="remember-me" />
                                <label htmlFor="remember-me">Recuerdame</label>
                            </div>
                            <Link to="/forgot-password" className="forgot-password">Olvide mi contraseña</Link>
                        </div>

                        <button type="submit" className="login-button">Ingresar</button>

                        {errorMessage && <p className="error-message">{errorMessage}</p>}
                    </form>

                    <p className="register-option">
                        No tienes cuenta? <Link to="/register">Registrarse</Link>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Login;
