import React, { useState, useEffect } from 'react';
import '../components/RegisterEventDetails.css';

const RegisterEventDetails = ({ eventData }) => {
    const [formData, setFormData] = useState({
        fechaInicio: '',
        horaInicio: '',
        fechaFin: '',
        horaFin: '',
        aperturaPuertas: '',
        modalidad: '',
        nombreLugar: '',
        ciudad: 'Cochabamba',
        enlaceUbicacion: '',
    });

    useEffect(() => {
        // Combinar los datos de la primera vista con los datos de esta vista
        setFormData((prevData) => ({
            ...prevData,
            ...eventData,
        }));
    }, [eventData]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Enviar los datos al backend
        const detallesEvento = {
            ...formData,
            idEvento: eventData.id,  // Incluye el ID del evento creado
        };

        try {
            const response = await fetch('http://localhost:3001/api/eventos/detalles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(detallesEvento),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Detalles del evento guardados con éxito:', data);
            } else {
                console.error('Error al guardar los detalles del evento');
            }
        } catch (error) {
            console.error('Error al enviar los detalles del evento:', error);
        }
    };

    return (
        <form className="register-event-details-form" onSubmit={handleSubmit}>
            <h2>Detalles del Evento</h2>

            <label>Fecha y hora de inicio del evento</label>
            <input
                type="date"
                name="fechaInicio"
                value={formData.fechaInicio}
                onChange={handleChange}
                required
            />
            <input
                type="time"
                name="horaInicio"
                value={formData.horaInicio}
                onChange={handleChange}
                required
            />

            <label>Fecha y hora de fin del evento</label>
            <input
                type="date"
                name="fechaFin"
                value={formData.fechaFin}
                onChange={handleChange}
                required
            />
            <input
                type="time"
                name="horaFin"
                value={formData.horaFin}
                onChange={handleChange}
                required
            />

            <label>Apertura de puertas</label>
            <input
                type="time"
                name="aperturaPuertas"
                value={formData.aperturaPuertas}
                onChange={handleChange}
                required
            />

            <label>Modalidad</label>
            <input
                type="text"
                name="modalidad"
                value={formData.modalidad}
                onChange={handleChange}
                required
            />

            <label>Nombre del lugar</label>
            <input
                type="text"
                name="nombreLugar"
                value={formData.nombreLugar}
                onChange={handleChange}
                required
            />

            <label>Ciudad</label>
            <input
                type="text"
                name="ciudad"
                value={formData.ciudad}
                onChange={handleChange}
                required
            />

            <label>Enlace de ubicación</label>
            <input
                type="url"
                name="enlaceUbicacion"
                value={formData.enlaceUbicacion}
                onChange={handleChange}
                required
            />

            <button type="submit">Guardar</button>
        </form>
    );
};

export default RegisterEventDetails;
