import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../components/RegisterEvent.css';

const RegisterEvent = () => {
    const [formData, setFormData] = useState({
        nombreEvento: '',
        descripcionEvento: '',
        tipoEvento: '',
        aforo: '',
        sectores: {
            vip: false,
            general: false,
            preferencia: false,
        },
        costos: {
            costoVip: '',
            costoGeneral: '',
            costoPreferencia: ''
        }
    });
    const [eventData, setEventData] = useState(null);  // Estado para almacenar los datos del evento
    const navigate = useNavigate();

    useEffect(() => {
        // Hacer una solicitud a la API para obtener los datos del evento
        axios.get('http://localhost:5000/api/eventos')  // Cambia esta URL por la correcta de tu API
            .then(response => {
                // Aquí puedes manipular los datos del evento que devuelves desde la API
                setEventData(response.data);
                // Asignar los valores del evento a formData para pre-poblar el formulario
                if (response.data.length > 0) {
                    setFormData({
                        nombreEvento: response.data[0].nombreEvento,
                        descripcionEvento: response.data[0].descripcionEvento,
                        tipoEvento: response.data[0].tipoEvento,
                        aforo: response.data[0].aforo,
                        sectores: {
                            vip: response.data[0].sectores.vip,
                            general: response.data[0].sectores.general,
                            preferencia: response.data[0].sectores.preferencia,
                        },
                        costos: {
                            costoVip: response.data[0].costos.costoVip,
                            costoGeneral: response.data[0].costos.costoGeneral,
                            costoPreferencia: response.data[0].costos.costoPreferencia,
                        }
                    });
                }
            })
            .catch(error => {
                console.error("Error fetching events data", error);
            });
    }, []);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (type === "checkbox") {
            setFormData({
                ...formData,
                sectores: {
                    ...formData.sectores,
                    [name]: checked
                }
            });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleCostoChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            costos: { ...formData.costos, [name]: value }
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Datos del evento:', formData);
        
        navigate('/registereventdetails');
    };

    return (
        <form className="register-event-form" onSubmit={handleSubmit}>
            <h2>Registro de evento</h2>
            <p>Información Básica del evento, detalles que hacen únicos cada evento.</p>

            <label>Nombre del evento</label>
            <input
                type="text"
                name="nombreEvento"
                placeholder="Nombre del evento"
                value={formData.nombreEvento}
                onChange={handleChange}
                required
            />

            <label>Descripción del evento</label>
            <input
                type="text"
                name="descripcionEvento"
                placeholder="Descripción del evento"
                value={formData.descripcionEvento}
                onChange={handleChange}
                required
            />

            <label>Tipo de evento</label>
            <input
                type="text"
                name="tipoEvento"
                placeholder="Tipo de evento"
                value={formData.tipoEvento}
                onChange={handleChange}
                required
            />

            <label>Aforo</label>
            <input
                type="number"
                name="aforo"
                placeholder="Cantidad"
                value={formData.aforo}
                onChange={handleChange}
                required
            />

            <label>Sectores</label>
            <div className="sectores">
                <div>
                    <input
                        type="checkbox"
                        name="vip"
                        checked={formData.sectores.vip}
                        onChange={handleChange}
                    />
                    <label>VIP</label>
                    {formData.sectores.vip && (
                        <input
                            type="number"
                            name="costoVip"
                            placeholder="Costo VIP"
                            value={formData.costos.costoVip}
                            onChange={handleCostoChange}
                        />
                    )}
                </div>
                <div>
                    <input
                        type="checkbox"
                        name="general"
                        checked={formData.sectores.general}
                        onChange={handleChange}
                    />
                    <label>General</label>
                    {formData.sectores.general && (
                        <input
                            type="number"
                            name="costoGeneral"
                            placeholder="Costo General"
                            value={formData.costos.costoGeneral}
                            onChange={handleCostoChange}
                        />
                    )}
                </div>
                <div>
                    <input
                        type="checkbox"
                        name="preferencia"
                        checked={formData.sectores.preferencia}
                        onChange={handleChange}
                    />
                    <label>Preferencia</label>
                    {formData.sectores.preferencia && (
                        <input
                            type="number"
                            name="costoPreferencia"
                            placeholder="Costo Preferencia"
                            value={formData.costos.costoPreferencia}
                            onChange={handleCostoChange}
                        />
                    )}
                </div>
            </div>

            <button type="submit">Siguiente</button>
        </form>
    );
};

export default RegisterEvent;
